    (
      <div>
        <div class="card" style={{width: "18rem"}}>
        <img src="..." class="card-img-top" alt="..."></img>
        <div class="card-body">
        <h5 class="card-title">{title}</h5>
        <p class="card-text">{description}</p>
        <a href="#" class="btn btn-primary">Go somewhere</a>
      </div>
      </div>
      </div>
    )
  }
}

export default NewsItem
